using System;
using System.Linq;
using Castle.Core.Logging;
using Kontecg.EFCore;
using Kontecg.Features;
using Kontecg.HumanResources;
using Kontecg.MimeTypes;
using Kontecg.MultiCompany;
using Kontecg.Storage;
using Microsoft.EntityFrameworkCore;

namespace Kontecg.Migrations.Seed.Host
{
    public class DefaultCompanyBuilder
    {
        private readonly KontecgCoreDbContext _context;
        private readonly ILogger _logger;

        public DefaultCompanyBuilder(KontecgCoreDbContext context, ILogger logger)
        {
            _context = context;
            _logger = logger;
        }

        public void Create()
        {
            CreateDefaultCompany();
        }

        private void CreateDefaultCompany()
        {
            //Default company

            var defaultCompany = _context.Companies.IgnoreQueryFilters().FirstOrDefault(t => t.CompanyName == KontecgCompanyBase.DefaultCompanyName);
            if (defaultCompany == null)
            {
                Address address = new("Carretera Moa-Baracoa, Km 5 1/2, Yagrumaje, Punta Gorda", "Moa", "Holguín", "Cuba", "83300", "+5324608012", "+5324602240");
                defaultCompany = new Company(KontecgCompanyBase.DefaultCompanyName, KontecgCompanyBase.DefaultName, Company.DefaultCompanyReup, address, "MINEM");

                var base64String =
    "";
                var mimetypeResolver = new MimeTypeMap();
                byte[] byteArray = Convert.FromBase64String(base64String);
                defaultCompany.ClearLetterHead();

                var bin = new BinaryObject(MultiCompanyConsts.DefaultCompanyId, byteArray,
                    $"Membrete para informes de la empresa {defaultCompany.CompanyName}. {DateTime.UtcNow}");

                _context.BinaryObjects.Add(bin);
                _context.SaveChanges();

                defaultCompany.LetterHeadId = bin.Id;
                defaultCompany.LetterHeadFileType = mimetypeResolver.GetMimeType("Membrete.png");

                _context.Companies.Add(defaultCompany);
                _context.SaveChanges();

                _logger.Info("Default company created.");

                if (defaultCompany.Id > 0)
                {
                    AddFeatureIfNotExists(defaultCompany.Id, CoreFeatureNames.ExportingFeature, true);
                    AddFeatureIfNotExists(defaultCompany.Id, CoreFeatureNames.ExportingPdfFeature, true);
                    AddFeatureIfNotExists(defaultCompany.Id, CoreFeatureNames.ExportingExcelFeature, true);
                    AddFeatureIfNotExists(defaultCompany.Id, CoreFeatureNames.CurrencyExchangeRateFeature, true);
                }
            }
        }

        private void AddFeatureIfNotExists(int companyId, string featureName, bool isEnabled)
        {
            var defaultFeature = _context.CompanyFeatureSettings.IgnoreQueryFilters()
                .FirstOrDefault(f => f.CompanyId == companyId && f.Name == featureName);

            if (defaultFeature == null)
            {
                _context.CompanyFeatureSettings.Add(new CompanyFeatureSetting()
                {
                    Name = featureName,
                    Value = isEnabled.ToString().ToLower()
                });
                _context.SaveChanges();
            }
        }
    }
}
